def crop4interpolation(x, scale):
	h = x.shape[0]
	w = x.shape[1]
	h = h - h%scale
	w = w - w%scale
	return x[0:h, 0:w]

def list_filter(file_list, tail):
	r = []
	for f in file_list:
		s = os.path.splitext(f)
		if s[1] == tail:
			r.append(f)
	return r
   
def get_Y(x):
	r = x[:,:,0]
	g = x[:,:,1]
	b = x[:,:,2]
	y = 0.257*r + 0.504*g + 0.098*b + 0.0627
	return y

def calc_PSNR(x,y):
	mse = np.mean(np.square(x-y))
	psnr = 10*math.log10(1/mse)
	return psnr

def get_session():
   config = tf.ConfigProto()
   config.gpu_options.allow_growth = True
   return tf.Session(config = config)

def get_table(x, QF):
   if QF < 50:
      scale = int(5000/QF)
   else:
      scale = 200 - QF*2
   _table = (x*scale+50)/100
   _table = _table.astype(np.int32)
   _table[_table>255]=255
   _table[_table<1] = 1
   _table = _table.astype(np.float32)
   _table = _table/255.0
   _table = _table.reshape(8,8)
   return _table

